import React, { createContext, useState, useContext, useEffect } from 'react';

const ForumContext = createContext();

export const ForumProvider = ({ children }) => {
  const [threads, setThreads] = useState([]);
  const [currentThread, setCurrentThread] = useState(null);
  const [sortBy, setSortBy] = useState('created');

  const fetchThreads = async () => {
    const response = await fetch(`http://localhost:5001/api/threads?sort=${sortBy}`);
    const data = await response.json();
    setThreads(data);
  };

  const fetchThread = async (id) => {
    const response = await fetch(`http://localhost:5001/api/threads/${id}`);
    const data = await response.json();
    setCurrentThread(data);
  };

  const createThread = async (title, content) => {
    const response = await fetch('http://localhost:5001/api/threads', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ title, content }),
    });
    const data = await response.json();
    fetchThreads();
    return data;
  };

  const createReply = async (threadId, content) => {
    const response = await fetch(`http://localhost:5001/api/threads/${threadId}/replies`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ content }),
    });
    const data = await response.json();
    fetchThread(threadId);
    fetchThreads();
    return data;
  };

  useEffect(() => {
    fetchThreads();
  }, [sortBy]);

  return (
    <ForumContext.Provider
      value={{
        threads,
        currentThread,
        sortBy,
        setSortBy,
        fetchThreads,
        fetchThread,
        createThread,
        createReply,
      }}
    >
      {children}
    </ForumContext.Provider>
  );
};

export const useForum = () => useContext(ForumContext);
