import React from 'react';
import { Link } from 'react-router-dom';
import { useForum } from '../context/ForumContext';

const ThreadList = () => {
  const { threads, sortBy, setSortBy } = useForum();

  return (
    <div className="thread-list">
      <div className="thread-list-header">
        <h2>Diskussionstrådar</h2>
        <div className="sort-controls">
          <label>Sortera efter: </label>
          <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
            <option value="created">Senast skapad</option>
            <option value="activity">Senaste aktivitet</option>
            <option value="replies">Antal svar</option>
          </select>
        </div>
      </div>

      <Link to="/new-thread" className="new-thread-button">
        Skapa ny tråd
      </Link>

      <div className="threads">
        {threads.map((thread) => (
          <div key={thread.id} className="thread-item">
            <Link to={`/thread/${thread.id}`}>
              <h3>{thread.title}</h3>
            </Link>
            <div className="thread-meta">
              <span>Skapad: {new Date(thread.created_at).toLocaleDateString()}</span>
              <span>Antal svar: {thread.reply_count}</span>
              {thread.last_activity && (
                <span>
                  Senaste aktivitet: {new Date(thread.last_activity).toLocaleDateString()}
                </span>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ThreadList;
