import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useForum } from '../context/ForumContext';

const ThreadDetail = () => {
  const { id } = useParams();
  const { currentThread, fetchThread, createReply } = useForum();
  const [replyContent, setReplyContent] = useState('');

  useEffect(() => {
    fetchThread(id);
  }, [id]);

  const handleSubmitReply = async (e) => {
    e.preventDefault();
    if (!replyContent.trim()) return;

    await createReply(id, replyContent);
    setReplyContent('');
  };

  if (!currentThread) {
    return <div>Laddar...</div>;
  }

  return (
    <div className="thread-detail">
      <h2>{currentThread.title}</h2>
      <div className="thread-content">
        <p>{currentThread.content}</p>
        <p className="thread-meta">
          Skapad: {new Date(currentThread.created_at).toLocaleDateString()}
        </p>
      </div>

      <div className="replies">
        <h3>Svar</h3>
        {currentThread.replies.map((reply) => (
          <div key={reply.id} className="reply">
            <p>{reply.content}</p>
            <p className="reply-meta">
              {new Date(reply.created_at).toLocaleDateString()}
            </p>
          </div>
        ))}
      </div>

      <form onSubmit={handleSubmitReply} className="reply-form">
        <textarea
          value={replyContent}
          onChange={(e) => setReplyContent(e.target.value)}
          placeholder="Skriv ditt svar hÃ¤r..."
        />
        <button type="submit">Skicka svar</button>
      </form>
    </div>
  );
};

export default ThreadDetail;
